import React, { useState } from 'react';
import { format } from 'date-fns';
import { he } from 'date-fns/locale';
import ApproveMessage from './approveMessage'


const Summary = ({ selectedDate, onBack,setProgress }) => {
  const [showApproveMessage, setShowApproveMessage] = useState(false);

  const formattedDate = format(selectedDate, 'dd/MM/yy', { locale: he });
  const dayNameInHebrew = format(selectedDate, 'EEEE', { locale: he });

  const handleConfirmButtonClick = () => {
    setShowApproveMessage(true);
    setProgress(3);
  };

  const handleBack = () => {
    setShowApproveMessage(false); 
    setProgress(1);
  };

  return (
    <div id=''>
    {ApproveMessage ? (
      <ApproveMessage selectedDate={selectedDate} onBack={handleBack} setProgress={setProgress} />
    ) : (
      <>
      <h2>סיכום</h2>
      <p>({dayNameInHebrew}) שיעור במתמטיקה בתאריך {formattedDate}</p>
      <p>ב11:00</p>
      <button onClick={onBack}>חזור</button>
      <button onClick={handleConfirmButtonClick} >אישור </button>

      </>
      )}
    </div>
  );
};

export default Summary;
