import React, { useState, useEffect } from 'react';
import './calendar.css';

const DateSelector = () => {
  const [currentDate, setCurrentDate] = useState(new Date());
  const [selectedDate, setSelectedDate] = useState(null);
  const [showContinueButton, setShowContinueButton] = useState(false);
  const [showHourSelector, setShowHourSelector] = useState(false);

  const daysInMonth = (date) => new Date(date.getFullYear(), date.getMonth() + 1, 0).getDate();

  const generateDates = () => {
    const firstDay = new Date(currentDate.getFullYear(), currentDate.getMonth(), 1);
    const startingDayIndex = firstDay.getDay(); // 0 for Sunday, 1 for Monday, etc.
    const totalDays = daysInMonth(currentDate);
    const dates = [];

  
  
    const handleContinueButtonClick = () => {
      setShowHourSelector(true);
    };
  
    const handleBack = () => {
      setShowHourSelector(false); // Set it to false instead of null
    };

    // Add empty cells for days before the start of the month
    for (let i = 0; i < startingDayIndex; i++) {
      dates.push(null);
    }

    // Add days of the month
    for (let i = 1; i <= totalDays; i++) {
      dates.push(new Date(currentDate.getFullYear(), currentDate.getMonth(), i));
    }

    return dates;
  };

  const handlePrevMonth = () => {
    setCurrentDate(new Date(currentDate.getFullYear(), currentDate.getMonth() - 1, 1));
  };

  const handleNextMonth = () => {
    setCurrentDate(new Date(currentDate.getFullYear(), currentDate.getMonth() + 1, 1));
  };

  const handleDateClick = (date) => {
    setSelectedDate(date);
    setShowContinueButton(true);

  };

  useEffect(() => {
    // You can perform any additional actions when the month changes
    console.log('Month changed:', currentDate.toLocaleString('default', { month: 'long' }));
  }, [currentDate]);

  return (
    <div>
      <h2>{currentDate.toLocaleString('default', { month: 'long', year: 'numeric' })}</h2>
      <button onClick={handlePrevMonth}>Previous Month</button>
      <button onClick={handleNextMonth}>Next Month</button>
      <table>
        <thead>
          <tr>
            <th>Sun</th>
            <th>Mon</th>
            <th>Tue</th>
            <th>Wed</th>
            <th>Thu</th>
            <th>Fri</th>
            <th>Sat</th>
          </tr>
        </thead>
        <tbody>
          {generateDates().reduce((rows, date, index) => {
            if (index % 7 === 0) rows.push([]);
            rows[rows.length - 1].push(date);
            return rows;
          }, []).map((week, rowIndex) => (
            <tr key={rowIndex}>
              {week.map((date, colIndex) => (
                <td key={colIndex}>
                  {date && (
                    <button onClick={() => handleDateClick(date)}>
                      {date.getDate()}
                    </button>
                  )}
                </td>
              ))}
            </tr>
          ))}
        </tbody>
      </table>
      {selectedDate && (
        <p>Selected Date: {selectedDate.toLocaleDateString()}</p>
      )}
      {showContinueButton && (
        <button onClick={handleContinueButtonClick}>המשך</button>
      )}
    </div>
  );
};

export default DateSelector;
