// SelectedDateComponent.js
import React, { useState } from 'react';
import { format } from 'date-fns';
import { he } from 'date-fns/locale';
import Summary from './summary';

const HourSelector = ({ selectedDate, onBack }) => {
  const [showContinueButton, setShowContinueButton] = useState(false);
  const [showSummary, setShowSummary] = useState(false);

  const formattedDate = format(selectedDate, 'dd/MM/yy', { locale: he });
  const dayNameInHebrew = format(selectedDate, 'EEEE', { locale: he });

  const handleTimeButtonClick = () => {
    // Add logic to handle the "11:00" button click
    setShowContinueButton(true);
    setShowSummary(true);

  };

  const handleContinueButtonClick = () => {
    setShowSummary(true);
  };

  const handleBack = () => {
    setShowSummary(false); 
  };

  return (
    <div>
      {showSummary ? (
        <Summary selectedDate={selectedDate} onBack={handleBack} />
      ) : (
        <>
          <h2>Selected Date</h2>
          <p>({dayNameInHebrew}) {formattedDate}</p>
          <button onClick={onBack}>חזור</button>
          <button onClick={handleTimeButtonClick}>11:00</button>

          {showContinueButton && (
            <button onClick={handleContinueButtonClick}>המשך</button>
          )}

          {/* Add additional content for the selected date component */}
        </>
      )}
    </div>
  );
};

export default HourSelector;
