import React, { useState } from 'react';
import { format, addMonths, subMonths, startOfMonth, startOfWeek, addDays, isSameMonth, isSameDay } from 'date-fns';
import { he } from 'date-fns/locale';
import SelectedDateComponent from './SelectedDateComponent';
import './calendar.css'

const Calendar = () => {
  const [currentDate, setCurrentDate] = useState(new Date());
  const [selectedDate, setSelectedDate] = useState(null);

  const nextMonth = () => setCurrentDate(addMonths(currentDate, 1));
  const prevMonth = () => setCurrentDate(subMonths(currentDate, 1));

  const startOfMonthDate = startOfMonth(currentDate);
  const startOfWeekDate = startOfWeek(startOfMonthDate);

  const calendar = [];
  let day = startOfWeekDate;
  
  let numOfWeeks=0;
  while (day < addDays(startOfWeekDate, 7 * 6)) {
    const week = [];
    let isDayInTheSameMonth=true;
    for (let i = 0; i < 7; i++) {
        week.push(day);
      
      day = addDays(day, 1);
    }
    for (let i = 0; i <7; i++) {
      if (!(isSameMonth(week[i], startOfMonthDate))) {
        isDayInTheSameMonth=false;
      }
    }
    if(isDayInTheSameMonth || numOfWeeks==0)
    calendar.push(week);
    numOfWeeks++;
  }

  const handleDateClick = (date) => {
    setSelectedDate(date);
  };

  const handleBack = () => {
    setSelectedDate(null);
  };

  return (
    <div>
      {selectedDate ? (
        <SelectedDateComponent selectedDate={selectedDate} onBack={handleBack} />
      ) : (
        <>
          <div>
            <button onClick={prevMonth}>{'<'}</button>
            <h2>{format(currentDate, 'MMMM yyyy', { locale: he })}</h2>
            <button onClick={nextMonth}>{'>'}</button>
          </div>
          <table>
            <thead>
              <tr>
                <th>Sat</th>
                <th>Fri</th>
                <th>Thu</th>
                <th>Wed</th>
                <th>Tue</th>
                <th>Mon</th>
                <th>Sun</th>
              </tr>
            </thead>
            <tbody>
              {calendar.map((week, index) => (
                <tr key={index}>
                {week.reverse().map((date) => (
                  <td key={date.toString()} className={isSameMonth(date, currentDate) ? 'currentMonth' : 'otherMonth'}>
                    <button onClick={() => handleDateClick(date)}>
                      {format(date, 'd')}
                    </button>
                  </td>
                ))}
              </tr>
              
              ))}
            </tbody>
          </table>
        </>
      )}
    </div>
  );
};

export default Calendar;
