import React, { useState } from 'react';
import { format } from 'date-fns';
import { he } from 'date-fns/locale';


const ApproveMessage = ({ selectedDate, onBack,setProgress }) => {
  const [showContinueButton, setShowContinueButton] = useState(false);

  const formattedDate = format(selectedDate, 'dd/MM/yy', { locale: he });
  const dayNameInHebrew = format(selectedDate, 'EEEE', { locale: he });

  

  return (
    <div>
      <h2>סיכום</h2>
      <p>{formattedDate} קבעת במתמטיקה בתאריך </p>
      <p>({dayNameInHebrew})</p>
      <p>ב11:00</p>
      <button onClick={onBack}>חזור</button>
      <button onClick={handleConfirmButtonClick}>אישור</button>


    </div>
  );
};

export default ApproveMessage;
