import React, { useState } from 'react';
import Calendar from 'react-calendar';
import 'react-calendar/dist/Calendar.css';

const MyCustomizedCalendar = () => {
  const [value, onChange] = useState(new Date());

  const customProps = {
    formatShortWeekday: (locale, date) => {
      // Customize the format of the short weekday here
      // Example: using date-fns for formatting
      return format(date, 'iiiiii', { locale });
    },
    formatWeekday: (locale, date) => {
      // Customize the format of the weekday here
      // Example: using date-fns for formatting
      return format(date, 'iiii', { locale });
    },
  };

  return (
    <div>
      <Calendar
        {...customProps}
        onChange={onChange}
        value={value}
      />
    </div>
  );
};

export default MyCustomizedCalendar;
