import React, { useState, useEffect } from 'react';
import './progressBar.css'

const ProgressBar = ({ progress }) => {
  const [prevProgre, setPrevCount] = useState(0);

  // const progressBarItems = [];

  // Assuming you want to display the progress bar 4 times
  // for (let i = 0; i < 4; i++) {
  //   progressBarItems.push(<div key={i} className="progress-bar"></div>);
  // }
  // const [progress, setProgress] = useState(0);

  //  const updateProgress = () => {
  //   if (progress < 100) {
  //     setProgress(progress + 10);
  //   }
  // };
  
  // useEffect(() => {
  //   setProgress(percentage);
  // }, [percentage]);
  return (
    <div id='progress-bars'>
      <h2>{progress}</h2>
      <div className="progress-bar-container">
      {(progress === 1 || progress === 2 || progress === 3 || progress === 4) && (
        <div className="progress-bar"></div>
        )}
      </div>
      <div className="progress-bar-container">
      {(progress === 2 || progress === 3 || progress === 4) && (
        <div className="progress-bar"></div>
        )}      </div>
      <div className="progress-bar-container">
      {(progress === 3 || progress === 4) && (
        <div className="progress-bar"></div>
        )}      </div>
      <div className="progress-bar-container">
      {progress === 4 && (
        <div className="progress-bar"></div>
        )}      </div>
    </div>
  );
};

export default ProgressBar;
